import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import { makeStyles } from '@material-ui/core/styles';
import { Button, Typography, TextField, TextareaAutosize, Card, CardContent } from '@material-ui/core';

const useStyles = makeStyles(theme => ({
  root: {
    maxWidth: 800,
    margin: 'auto',
    marginTop: theme.spacing(3),
  },
  card: {
    marginBottom: theme.spacing(2),
  },
  form: {
    border: '1px solid #ccc',
    padding: theme.spacing(2),
    marginBottom: theme.spacing(2),
  },
  input: {
    marginBottom: theme.spacing(2),
    width: '100%',
  },
}));

const AddCampusForm = ({ onSubmit }) => {
  const classes = useStyles();
  const [formData, setFormData] = useState({
    name: "",
    address: "",
    description: "",
    imageUrl: ""
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevData => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData);
    setFormData({
      name: "",
      address: "",
      description: "",
      imageUrl: ""
    });
  };

  return (
    <div className={classes.form}>
      <Typography variant="h6">Add Campus</Typography>
      <form onSubmit={handleSubmit}>
        <TextField
          label="Name"
          name="name"
          value={formData.name}
          onChange={handleChange}
          className={classes.input}
        />
        <TextField
          label="Address"
          name="address"
          value={formData.address}
          onChange={handleChange}
          className={classes.input}
        />
        <TextareaAutosize
          aria-label="Description"
          name="description"
          placeholder="Description"
          minRows={3}
          value={formData.description}
          onChange={handleChange}
          style={{ width: '100%', marginBottom: 16 }}
        />
        <TextField
          label="Image URL"
          name="imageUrl"
          value={formData.imageUrl}
          onChange={handleChange}
          className={classes.input}
        />
        <Button type="submit" variant="contained" color="primary">Submit</Button>
      </form>
    </div>
  );
};

const AllCampusesView = ({ allCampuses }) => {
  const classes = useStyles();

  const handleAddCampus = formData => {
    // POST request logic here
  };

  const handleDeleteCampus = id => {
    // DELETE request logic here
  };

  const handleEditCampus = id => {
    // Redirect to edit campus logic here
  };

  return (
    <div className={classes.root}>
      <Typography variant="h4">All Campuses</Typography>
      {allCampuses.length ? (
        allCampuses.map(campus => (
          <Card key={campus.id} className={classes.card}>
            <CardContent>
              <Link to={`/campus/${campus.id}`}>
                <Typography variant="h5">{campus.name}</Typography>
              </Link>
              <Typography variant="body1">{campus.address}</Typography>
              <Typography variant="body2">{campus.description}</Typography>
              <Button onClick={() => handleEditCampus(campus.id)} color="primary">Edit</Button>
              <Button onClick={() => handleDeleteCampus(campus.id)} color="secondary">Delete</Button>
            </CardContent>
          </Card>
        ))
      ) : (
        <Typography variant="body1">There are no campuses.</Typography>
      )}
      <AddCampusForm onSubmit={handleAddCampus} />
    </div>
  );
};

AddCampusForm.propTypes = {
  onSubmit: PropTypes.func.isRequired,
};

AllCampusesView.propTypes = {
  allCampuses: PropTypes.array.isRequired,
};

export default AllCampusesView;
