/*==================================================
AllCampusesView.js

The Views component is responsible for rendering web page with data provided by the corresponding Container component.
It constructs a React component to display all campuses.
================================================== */
import React, { useState } from "react";
import PropTypes from "prop-types";
import { Link } from "react-router-dom";
import { Button, Typography } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({

  root: {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
    backgroundImage: 'linear-gradient(to bottom right, #6a11cb, #2575fc)',
    color: '#ffffff',
    textAlign: 'center',
    overflow: 'hidden',
  },  
  reet: {

    justifyContent: 'center',
    backgroundImage: 'linear-gradient(to bottom right, #6a11cb, #2575fc)',
    color: '#ffffff', // White text color
    textAlign: 'center',
    overflow: 'hidden', // Hide overflowing content
    color: "white"
  },
  studentContainer: {
    width: '80%', // Manageable width for each student container
    maxWidth: '600px', // Maximum width to keep the UI clean
    margin: '20px auto', // Centering and spacing vertically
    padding: '10px',
    backgroundColor: '#e3e6f0', // Light gray background for each student
    borderRadius: '5px',
    boxShadow: '0px 2px 5px rgba(0, 0, 0, 0.15)',
    textAlign: 'center', // Center text elements within the student container
    color: "black",
    marginBottom: theme.spacing(2), // Adjust margin for the campus container
  },
  studentName: {
    fontSize: '1.2rem',
    color: '#2a2f45',
    marginBottom: '10px',
  },
}));





//----------------------------------------------Edit campus form-------------------------//
const EditCampusForm = ({ onSubmit, onCancel, campus }) => {
  console.log("EditCampusForm running")
  const [formData, setFormData] = useState({
    id: campus.id,
    name: campus.name,
    address: campus.address,
    description: campus.description,
    imageUrl: campus.imageUrl,
  });

  const [errors, setErrors] = useState({
    imageUrl: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));


    // Reset the error message when user starts typing
    setErrors((prevErrors) => ({
      ...prevErrors,
      [name]: "",
    }));
  };

  const validate = () => {
    let isValid = true;
    const newErrors = {};

    // Validate imageUrl
    if (formData.imageUrl && !formData.imageUrl.endsWith(".jpg")) {
      newErrors.imageUrl = "Image URL must end with .jpg";
      isValid = false;
    }

    setErrors(newErrors);
    return isValid;
  };





  const handleSubmit = (e) => {
    e.preventDefault();

    if (validate()) {
      onSubmit(formData);
    }
  };

  const handleCancel = () => {
    onCancel();
  };

  return (
    <section style={{ border: "1px solid #ccc", padding: "20px", marginBottom: "20px" }}>
      <h2>Edit Campus</h2>
      <form onSubmit={handleSubmit}>
        <div style={{ marginBottom: "10px" }}>
          <label htmlFor="name">Name:</label>
          <input
            type="text"
            id="name"
            name="name"
            value={formData.name}
            onChange={handleChange}
          />
        </div>
        <div style={{ marginBottom: "10px" }}>
          <label htmlFor="address">Address:</label>
          <input
            type="text"
            id="address"
            name="address"
            value={formData.address}
            onChange={handleChange}
          />
        </div>
        <div style={{ marginBottom: "10px" }}>
          <label htmlFor="description">Description:</label>
          <textarea
            id="description"
            name="description"
            value={formData.description}
            onChange={handleChange}
          />
        </div>
        <div style={{ marginBottom: "10px" }}>
