const AddCampusForm = ({ onSubmit }) => {
  const [formData, setFormData] = useState({
    name: "",
    address: "",
    description: "",
    imageUrl: ""
  });

  const [errors, setErrors] = useState({
    imageUrl: ""
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));

    // Reset error message when user starts typing
    setErrors((prevErrors) => ({
      ...prevErrors,
      [name]: "",
    }));
  };


  const validate = () => {
    let isValid = true;
    const newErrors = {};

    // Validate imageUrl
    if (formData.imageUrl && !formData.imageUrl.endsWith(".jpg")) {
      newErrors.imageUrl = "Image URL must end with .jpg";
      isValid = false;
    }

    setErrors(newErrors);
    return isValid;
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (validate()) {
      onSubmit(formData);

      // Clear form fields after successful submission
      setFormData({
        name: "",
        address: "",
        description: "",
        imageUrl: ""
      });
    }
  };
  const classes = useStyles();



  return (
    <div className={classes.root}>

    <section style={{ border: "1px solid #ccc", padding: "20px", marginBottom: "20px" }}>
      <h2>Add Campus</h2>
      <form onSubmit={handleSubmit}>
        <div style={{ marginBottom: "10px" }}>
          <label htmlFor="name">Name:</label>
          <input
            type="text"
            id="name"
            name="name"
            value={formData.name}
            onChange={handleChange}
          />
        </div>
        <div style={{ marginBottom: "10px" }}>
          <label htmlFor="address">Address:</label>
          <input
            type="text"
            id="address"
            name="address"
            value={formData.address}
            onChange={handleChange}
          />
        </div>
        <div style={{ marginBottom: "10px" }}>
          <label htmlFor="description">Description:</label>
          <textarea
            id="description"
            name="description"
            value={formData.description}
            onChange={handleChange}
          />
        </div>
        <div style={{ marginBottom: "10px" }}>
          <label htmlFor="imageUrl">Image URL:</label>
          <input
            type="text"
            id="imageUrl"
            name="imageUrl"
            value={formData.imageUrl}
            onChange={handleChange}
          />
          {errors.imageUrl && <span style={{ color: "red" }}>{errors.imageUrl}</span>}
        </div>
        <button type="submit">Submit</button>
      </form>
    </section>
    </div>
  );
};


