import React from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { Button, Typography, Snackbar } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import MuiAlert from '@material-ui/lab/Alert';

const useStyles = makeStyles(theme => ({
  root: {
    height: '100vh',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    backgroundImage: 'linear-gradient(to bottom right, #6a11cb, #2575fc)', // Enhanced gradient background
    color: '#ffffff',
    textAlign: 'center',
    overflow: 'hidden',
  },
  formContainer: {
    width: '500px',
    backgroundColor: '#f0f0f5',
    borderRadius: '5px',
    margin: 'auto',
    boxShadow: '0px 0px 20px rgba(0, 0, 0, 0.2)', // Enhanced box shadow
    padding: theme.spacing(3),
    textAlign: 'center',
    transition: 'transform 0.3s ease-in-out', // Smooth transform transition
    '&:hover': {
      transform: 'scale(1.05)', // Scale effect on hover
    }
  },
  formTitle: {
    backgroundColor: '#c5c8d6',
    marginBottom: '15px',
    textAlign: 'center',
    borderRadius: '5px 5px 0px 0px',
    padding: '5px',
    boxShadow: '0px 2px 4px rgba(0, 0, 0, 0.1)',
    fontSize: '1.5rem',
  },
  studentContainer: {
    margin: '20px',
    padding: '10px',
    backgroundColor: '#e3e6f0',
    borderRadius: '5px',
    boxShadow: '0px 2px 5px rgba(0, 0, 0, 0.15)',
  },
  studentName: {
    fontSize: '1.2rem',
    color: '#2a2f45',
    marginBottom: '10px',
  },
}));

// Alert component
function Alert(props) {
  return <MuiAlert elevation={6} variant="filled" {...props} />;
}

const CampusView = (props) => {
  const { campus } = props;
  const classes = useStyles();
  const [open, setOpen] = React.useState(false);
  const [message, setMessage] = React.useState('');
  const [severity, setSeverity] = React.useState('success');

  const handleClose = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }
    setOpen(false);
  };

  const handleAddStudent = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/api/students', {
        firstname: e.target.firstname.value,
        lastname: e.target.lastname.value,
        email: e.target.email.value,
        imageUrl: e.target.imageUrl.value,
        gpa: e.target.gpa.value,
        campusId: campus.id,
      });

      setMessage('New student added successfully!');
      setSeverity('success');
      setOpen(true);
      window.location.href = '/campuses';
    } catch (error) {
      setMessage('Error adding student');
      setSeverity('error');
      setOpen(true);
    }
  };

  const handleDeleteStudent = async (studentId) => {
    try {
      await axios.delete(`/api/students/${studentId}`);
      setMessage('Student deleted successfully!');
      setSeverity('success');
      setOpen(true);
      window.location.reload();
    } catch (error) {
      setMessage('Error deleting student');
      setSeverity('error');
      setOpen(true);
    }
  };

  return (
    <div className={classes.root}>
      <Typography variant="h3">{campus.name}</Typography>
      <Typography variant="subtitle1">{campus.address}</Typography>
      <Typography variant="body1">{campus.description}</Typography>

      {campus.students.length > 0 ? (
        campus.students.map(student => {
          let name = student.firstname + " " + student.lastname;
          return (
            <div key={student.id} className={classes.studentContainer}>
              <div className={classes.studentName}>
                <Link to={`/student/${student.id}`}>
                  <Typography variant="h6">{name}</Typography>
                </Link>
              </div>
              <Button
                variant="contained"
                color="secondary"
                onClick={() => handleDeleteStudent(student.id)}
              >
                Delete
              </Button>
            </div>
          );
        })
      ) : (
        <Typography>No students are currently enrolled in this campus.</Typography>
      )}

      <div className={classes.formContainer}>
        <div className={classes.formTitle}>
          <Typography style={{ fontWeight: 'bold', color: '#11153e' }}>Add a Student</Typography>
        </div>
        <form onSubmit={handleAddStudent}>
          <input type="text" name="firstname" placeholder="First Name" required />
          <input type="text" name="lastname" placeholder="Last Name" required />
          <input type="email" name="email" placeholder="Email" required />
          <input type="text" name="imageUrl" placeholder="Image URL" />
          <input type="number" name="gpa" placeholder="GPA" step="0.01" required />
          <Button variant="contained" color="primary" type="submit">
            Submit
          </Button>
        </form>
      </div>

      <Snackbar open={open} autoHideDuration={6000} onClose={handleClose}>
        <Alert onClose={handleClose} severity={severity}>
          {message}
        </Alert>
      </Snackbar>
    </div>
  );
};

export default CampusView;
